class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        //å»ºç«‹ä¸€ä¸ªvector<int> Kçš„æ•°ç»„ï¼Œç„¶åä¸¤ä¸ªvectorè¿›è¡Œç›¸åŠ .
        queue<int> Kdigit;
        while(k){
            Kdigit.push(k % 10);k = k / 10;
        }
        //ç¬¬äºŒæ­¥ï¼Œå°†æ ˆä¸­çš„å…ƒç´ å’Œnumè¿›è¡Œç›¸åŠ .
        deque<int> ans;int endpoint = ans.size() - 1;
        //è®¾ç½®ä¸€ä¸ªæœ€å¤§é•¿åº¦çš„ans.
        int numpoint = num.size() - 1;int plus = 0;
        while(!Kdigit.empty() && numpoint >= 0){

            ans.push_front((plus + Kdigit.front() + num[numpoint]) % 10);
            plus = (plus + Kdigit.front() + num[numpoint]) / 10;
            Kdigit.pop();numpoint--;endpoint--;
        }
        //ç»“æŸæ—¶åˆ¤æ–­æœ€åçš„æ¡ä»¶.
        while(!Kdigit.empty()){  //è‹¥æ•°å­—ä¸ä¸ºç©º
            ans.push_front((plus + Kdigit.front()) % 10);
