            Kdigit.pop();
        }

        while(numpoint >= 0){  //è‹¥åŸæ•°ç»„ä¸ä¸ºç©º
            ans.push_front((plus + num[numpoint]) % 10);
            plus = (plus + num[numpoint]) / 10;
            numpoint--;
        }
        if(plus)
            ans.push_front(plus);

        vector<int> result(ans.begin(),ans.end());
        return result;
    }
};
