public class Solution {
    public IList<int> AddToArrayForm(int[] num, int k) {
        var kLen = k.ToString().Length;
            int[] kArr = new int[kLen];
            for (var i = kLen - 1; i >= 0; i--)
            {
                if (k / 10 > 0)
                {
                    kArr[i] = k % 10;
                    k = k / 10;
                }
                else
                {
                    kArr[i] = k;
                }
            }

            var addExtra = false;
            var nLen = num.Length;
            var list = new List<int>();
            for (int i = nLen - 1, j = kLen - 1; i >= 0 || j >= 0; i--, j--)
            {
                var nValue = (i > nLen - 1 || i < 0) ? 0 : num[i];
                var kValue = (j > kLen - 1 || j < 0) ? 0 : kArr[j];
                var sum = nValue + kValue + (addExtra ? 1 : 0);
                if (sum >= 10)
                {
                    addExtra = true;
                    sum = sum % 10;
                }
                else
                {
                    addExtra = false;
                }

                list.Add(sum);
            }

            if (addExtra)
            {
                list.Add(1);
            }
            list.Reverse();

            return list;
    }
}

