```var addToArrayForm = function(num, k) {
  let isOverflow = false, index = num.length - 1, res = [];
  while(index > -1 || k) {
     const num1 = num[index] || 0, num2 = k % 10;
     let sum = num1 + num2;
     if(isOverflow) {
          sum = sum + 1;
     }
      isOverflow = sum >= 10;
      res.unshift(sum % 10);
      k = (k / 10) | 0;
      index--;
      
  }
  if(isOverflow) res.unshift(1);
  return res;
};
