class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        //ç”¨äºæ ‡è¯†æ•°ç»„çš„ä½ç½®
        int len = num.length - 1;
        //ç”¨äºæ ‡è¯†æ˜¯å¦å‘ç”Ÿè¿›ä½
        int isCarry = 0;
        //ç»Ÿè®¡æ¯ä¸€è½®çš„å€¼ï¼Œæå‰åˆ›å»ºï¼Œå¢åŠ æ•ˆç‡
        int val = 0;
        //ç”¨äºä¿å­˜ç»“æœï¼Œä½¿ç”¨å¤´æ’æ³•æ’å…¥é“¾è¡¨
        List<Integer> res = new LinkedList<>();
        while(len > -1 || k > 0){
            if(k > 0){
                val = k % 10;
                k = k / 10;
            }
            if(len > -1){
                val += num[len];
            }
            val += isCarry;
            isCarry = val / 10;
            val = val % 10;
            res.add(0,val);
            len--;
            val = 0;
        }
        if(isCarry > 0){
            res.add(0,1);
        }
        return res;
    }
}
