    vector<int> addToArrayForm(vector<int>& num, int k) {
        queue<int> temp;
        stack<int> res;
        while(k!=0){
            temp.push(k%10);
            k = k/10;
        }
        int i=num.size()-1;
        int jinwei = 0;
        while(i>=0 && !temp.empty()){
            res.push((num[i]+temp.front()+jinwei)%10);
            jinwei = (num[i]+temp.front()+jinwei)/10;
            i--;
            temp.pop();
        }
        if(i>=0){
