                jinwei = (temp.front()+jinwei)/10;
                temp.pop();
            }
            
        }
        if(jinwei>0){
            res.push(jinwei);
        }
        vector<int> m;
        while(!res.empty()){
            m.push_back(res.top());
            res.pop();
        }
        return m;
    }
};
