var addToArrayForm = function(num, k) {
  const kArr = (k + '').split('')
  const ret = []

  let i = num.length - 1
  let j = kArr.length - 1
  let tmp = 0
  while (i >= 0 || j >= 0 || tmp) {
    const count = parseInt(num[i] || 0) + parseInt(kArr[j] || 0) + tmp
    ret.unshift(count % 10)
    tmp = parseInt(count / 10)
    i--
    j--
  }
  return ret
};
// @lc code=end

