class Solution {
    public static List<Integer> addToArrayForm(int[] num, int k) {
        LinkedList<Integer> res = new LinkedList<>();
        int carry = 0;      //è¿›ä½æ•°ï¼Œ1è¡¨ç¤ºè¿›ä½ï¼Œ0è¡¨ç¤ºæ— è¿›ä½
        int sum;    //æ¯æ¬¡æ±‚å’Œçš„å€¼
