public class Solution {

    public List<Integer> addToArrayForm(int[] num, int k) {
        ArrayList<Integer> list = new ArrayList<Integer>();
        for (int i = num.length - 1; i >= 0; i--) {
            int temp = num[i] + k % 10;
            k /= 10;
            if (temp >= 10){
                k++;
                temp -= 10;
            }
            list.add(temp);
        }

        while (k > 0){
            list.add(k % 10);
            k /= 10;
        }

        Collections.reverse(list);
        return list;
    }
}

