/**
 * @param {number[]} A
 * @param {number} K
 * @return {number[]}
 */
var addToArrayForm = (A, K) => {
    let len = A.length // æ•°ç»„Açš„é•¿åº¦
    let carryNum = 0 // è¿›ä½
    A.forEach((item, index) => {
        let curNum = Math.floor(K % Math.pow(10, index + 1) / Math.pow(10, index))
        let tmpCurNum = (curNum + A[len - index -1 ] + carryNum)
        A[len - index - 1] = tmpCurNum % 10
        carryNum = Math.floor(tmpCurNum / 10)
    })
    let kStrLen = (K + '').length
    // å¦‚æœKçš„ä½æ•°å¤§äºAå¯¹åº”çš„æ•°å­—çš„ä½æ•°
    if (kStrLen >= len) {
        let tmpK = Math.floor(K / Math.pow(10, len)) + carryNum
        let tmpKStrLen = (tmpK + '').length
        for (let i = 0; i < tmpKStrLen; i ++) {
            let tmpNum = Math.floor(tmpK % Math.pow(10, i + 1) / Math.pow(10, i))
            i === tmpKStrLen - 1 && tmpNum === 0 ? '' : A.unshift(tmpNum)
        }
    } else {
        carryNum ? A.unshift(carryNum) : ''
    }
    return A
}
```

