function addToArrayForm(num: number[], k: number): number[] {
    let kArr: number[] = String(k).split('').map(v => parseInt(v, 10));
    let kLength = kArr.length - 1;
    let numLength = num.length - 1;
    let flag: number = 0;
    while(numLength >= 0 || kLength >= 0 || flag > 0) {
        let kItem = kArr[kLength] || 0;
        let numItem = num[numLength] || 0;
        let sum = numItem + kItem + flag;
        flag = 0;
        if(sum >= 10) {
            flag = 1;
            sum -= 10;
        }
        if(numLength < 0) {
            num.unshift(sum);
        }else {            
            num[numLength] = sum;
        }
        numLength--;
        kLength--;
    }
    return num;
};
