class Solution {
    public int mySqrt(int x) {
        int left = 0, right = x;

        while (left < right - 1) {
            int mid = left + (right - left) / 2;
            long midSquare = 1l * mid * mid;
            if (midSquare == x) {
                return mid;
            } else if (midSquare < x) {
                left = mid;
            } else {
                right = mid - 1;
            }
        }
        long rightSqaure = 1l * right * right;
        return rightSqaure > x ? left : right;
    }
}
