class Solution {
    public int findRadius(int[] houses, int[] heaters) {
        Arrays.sort(houses);
        Arrays.sort(heaters);

        int left = 0, right = Integer.MAX_VALUE;
        while (left < right) {
            int mid = left + (right - left) / 2;
            boolean res = possible(houses, heaters, mid);
            if (res) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }

        return left;
    }

    private boolean possible(int[] houses, int[] heaters, int r) {
        int i = 0, j = 0, m = houses.length, n = heaters.length;
        while (i < m && j < n) {
            if (houses[i] >= heaters[j] - r && houses[i] <= heaters[j] + r) {
                ++i;
            } else if (houses[i] < heaters[j] - r) {
                return false;
            } else {
                ++j;
            }
        }
        return i >= m;
    }
}
