class Solution {
    public int smallestDistancePair(int[] nums, int k) {
        int n = nums.length;
        Arrays.sort(nums);
        int left = 0, right = nums[n - 1] - nums[0];
        while (left < right) {
            int mid = left + (right - left) / 2;
            int cnt = countSmEq(nums, mid);
						// If there are more than k pairs, then mid can be a possible solution
            if (cnt >= k) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }
        return left;
    }
    
    private int countSmEq(int[] nums, int diff) {
        int n = nums.length, fast = 1, slow = 0, cnt = 0;
        while (slow < n - 1) {
            while (fast < n && nums[fast] - nums[slow] <= diff) {
                ++fast;
            }
            cnt += fast - slow++ - 1;
        }
        return cnt;
    }
}
