class Solution {
    private int[][] dirs = {{1, 0}, {0, 1}, {-1, 0}, {0, -1}};
    public int swimInWater(int[][] grid) {
        int n = grid.length, left = 0, right = 50 * 50;
        while (left < right) {
            int mid = left + (right - left) / 2;
            if (check(grid, mid)) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }
        return left;
    }

    private boolean check(int[][] grid, int t) {
        int n = grid.length;
        Queue<int[]> queue = new ArrayDeque<>();
        boolean[][] visited = new boolean[n][n];
        if (grid[0][0] <= t) {
            queue.offer(new int[] {0, 0});
            visited[0][0] = true;    
        }
        while (!queue.isEmpty()) {
            int[] pos = queue.poll();
            if (pos[0] == n - 1 && pos[1] == n - 1) {
                return true;
            }
            for (int[] dir : dirs) {
                int x = pos[0] + dir[0], y = pos[1] + dir[1];
                if (x < 0 || x >= n || y < 0 || y >= n || visited[x][y] || grid[x][y] > t) {
                    continue;
                }

                queue.offer(new int[] {x, y});
                visited[x][y] = true;
            }
        }

        return false;
    }
}
