class Solution {
    public double new21Game(int n, int k, int maxPts) {
        if (k - 1 + maxPts <= n) {
            return 1.0;
        }
        double[] dp = new double[k + maxPts];
        double sum = 0.0;
        for (int i = k; i <= n; i++) {
            dp[i] = 1.0;
            sum += dp[i];
        }
        int left = k, right = k - 1 + maxPts;
        for (int i = k - 1; i >= 0; i--) {
            dp[i] = sum / maxPts;
            sum -= dp[right--];
            sum += dp[--left];
        }
        return dp[0];
    }
}
