class Solution {
    public String minWindow(String s, String t) {
        int m = s.length();
        Map<Character, Integer> freqOfT = getFreq(t);

        // {startIndex, len}
        int[] res = new int[2];
        int left = 0, right  = 0;
        Map<Character, Integer> freqOfS = new HashMap<>();

        while (right < m) {
            while (right < m && !check(freqOfS, freqOfT)) {
                freqOfS.put(s.charAt(right), freqOfS.getOrDefault(s.charAt(right), 0) + 1);
                ++right;
            }

            while (left < right && check(freqOfS, freqOfT)) {
                int len = right - left;
                if (res[1] == 0 || len < res[1]) {
                    res[0] = left;
                    res[1] = len;
                }

                freqOfS.computeIfPresent(s.charAt(left), (c, cnt) -> cnt == 1 ? null : cnt - 1);
                ++left;
            }
        }

        return s.substring(res[0], res[0] + res[1]);

    }
    private boolean check(Map<Character, Integer> sMap, Map<Character, Integer> tMap) {
        for (var entry : tMap.entrySet()) {
            char c = entry.getKey();
            int tCount = entry.getValue();
            int sCount = sMap.getOrDefault(c, 0);

            if (tCount > sCount) {
                return false;
            }
        }

        return true;
    }
    private Map<Character, Integer> getFreq(String t) {
        Map<Character, Integer> freq = new HashMap<>();

        for (int i = 0; i < t.length(); ++i) {
            freq.put(t.charAt(i), freq.getOrDefault(t.charAt(i), 0) + 1);
        }

        return freq;
    }
}
