class Solution {
    public int maxScore(int[] cards, int k) {
        int n = cards.length, len = n - k, left = 0, right = 0, minSum = Integer.MAX_VALUE, sum = 0;
        while (right - left < len) {
            sum += cards[right];
            right++;
        } 

        while (right < n) {
            minSum = Math.min(minSum, sum);
            sum -= cards[left];
            left++;
            sum += cards[right];
            right++;
        }

        minSum = Math.min(minSum, sum);
        int cardSum = 0;
        for (int card : cards) {
            cardSum += card;
        }
        return cardSum - minSum;
    }
}
