class Solution {
    private int[] watch = {1, 2, 4, 8, 1, 2, 4, 8, 16, 32};
    List<String> ret = new ArrayList<>();
    public List<String> readBinaryWatch(int turnedOn) {
        boolean[] chosen = new boolean[watch.length];
        dfs(0, turnedOn, chosen);
        return ret;
    }

    private void dfs(int idx, int turnedOn, boolean[] chosen) {
        if (idx == watch.length || turnedOn == 0) {
            if (turnedOn != 0) {
                return;
            }
            int hour = 0, min = 0;
            for (int i = 0; i < watch.length; i++) {
                if (chosen[i]) {
                    if (i < 4) {
                        hour += watch[i];
                    } else {
                        min += watch[i];
                    }
                }
            }    

            if (min >= 60 || hour > 11) {
                return;
            }

            StringBuilder sb = new StringBuilder();
            sb.append(hour);
            sb.append(':');
            if (min < 10) {
                sb.append('0');
            }
            sb.append(min);
            ret.add(sb.toString());
            return;
        }

        chosen[idx] = true;
        dfs(idx + 1, turnedOn - 1, chosen);
        chosen[idx] = false;
        dfs(idx + 1, turnedOn, chosen);
    }
}
