```java
class Solution {
    private int res;

    public int totalNQueens(int n) {
        List<Integer> board = new ArrayList<>();
        this.res = 0;
        backtrack(board, n);
        return this.res;
    }


    private void backtrack(List<Integer> board, int n) {
        if (board.size() == n) {
            ++res;
            return;
        }

        for (int i = 1; i <= n; ++i) {
            if (check(board, board.size() + 1, i)) {
                board.add(i);
                backtrack(board, n);
                board.remove(board.size() - 1);
            }
        }
    }

    private boolean check(List<Integer> board, int x, int y) {
        if (board.isEmpty()) {
            return true;
        }

        for (int i = 0; i < board.size(); ++i) {
            if (board.get(i) == y || Math.abs(i + 1 - x) == Math.abs(board.get(i) - y)) {
                return false;
            }
        }

        return true;
    }
}
