/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public List<List<Integer>> verticalTraversal(TreeNode root) {
        if (root == null) return null;
        // col -> {row, val}
        Map<Integer, List<int[]>> map = new HashMap<>();
        dfs(root, 0, 0, map);
        List<List<Integer>> res = new ArrayList<>();
        for (int i = -1000; i <= 1000; ++i) {
            List<int[]> l = map.getOrDefault(i, null);
            if (l == null) continue;
            Collections.sort(l, (t1, t2) -> {
                if (t1[0] == t2[0]) {
                    return t1[1] - t2[1];
                } else {
                    return t1[0] - t2[0];
                }
            });
            List<Integer> list = new ArrayList<>();
            for (int[] tuple : l) {
                list.add(tuple[1]);
            }
            res.add(list);
        }
        return res;
    }

    private void dfs(TreeNode node, int x, int y, Map<Integer, List<int[]>> map) {
        List<int[]> list = map.getOrDefault(x, new ArrayList<>());
        list.add(new int[] {y, node.val});
        map.put(x, list);
        if (node.left != null)
            dfs(node.left, x - 1, y + 1, map);
        if (node.right != null)
            dfs(node.right, x + 1, y + 1, map);
    }    
}
