class Solution {
    public int findNumberOfLIS(int[] nums) {
        int n = nums.length;
        int[] counts = new int[n], lens = new int[n];
        int ret = 0, maxLen = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0) {
                counts[i] = 1;
                lens[i] = 1;
            } else {
                int curLen = 1, curCnt = 1;
                for (int j = 0; j < i; j++) {
                    if (nums[i] <= nums[j]) {
