class Solution {
    private Map<Pair<Integer, Integer>, Boolean> memo;
    public boolean canIWin(int maxNum, int total) {
        if ((1 + maxNum) * maxNum / 2 < total) {
            return false;
        }
        memo = new HashMap<>();
        return dfs(total, 0, maxNum);
    }


    private boolean dfs(int remain, int state, int maxNum) {
        Pair<Integer, Integer> pair = new Pair(remain, state);
        if (memo.containsKey(pair)) {
            return memo.get(pair);
        }

        boolean res = false;
        for (int i = maxNum; i >= 1; i--) {
            if (getKthBit(state, i - 1) == 1) {
                continue;
            }

            if (remain <= i) {
                res = true;
            } else {
                res |= !dfs(remain - i, setKthBit(state, i - 1, 1), maxNum);
            }

            if (res) {
                break;
            }
        }

        memo.put(pair, res);
        return res;
    }

    private int getKthBit(int num, int k) {
        return (num >> k) & 1;
    }

    private int setKthBit(int num, int k, int val) {
        int kthBit = getKthBit(num, k);
        return num ^ ((kthBit ^ val) << k);
    }
}
