        Arrays.sort(intervals, (p1, p2) -> p1[0] - p2[0]);
        int idx = 0, ret = 0;
        while (idx < n) {
            int end = intervals[idx++][1];
            while (idx < n && intervals[idx][0] < end) {
                end = Math.min(end, intervals[idx][1]);
                ret++;
                idx++;
            }
        }
        return ret;
    }
}
