class Solution {
    public int numTrees(int n) {
        int[] dp = new int[n + 1];
        for (int i = 1; i <= n; ++i) {
            if (i == 1) {
                dp[i] = 1;
            } else {
                for (int j = 1; j <= i; ++j) {
                    if (j == 1 || j == i) {
                        dp[i] += dp[i - 1];
                    } else {
                        dp[i] += (dp[j - 1] * dp[i - j]);
                    }
                }
            }
        }

        return dp[n];
    }
}
