    private void insertWithDiff(String key, int diff) {
        TrieNode cur = root;
        int n = key.length();
        for (int i = 0; i < n; i++) {
            char c = key.charAt(i);
            if (!cur.children.containsKey(c)) {
                cur.children.put(c, new TrieNode(c));
            }
            cur = cur.children.get(c);
            cur.sum += diff;
            if (i == n - 1) {
                cur.val += diff;
            }
        }
    }

