class Solution {
    private TrieNode root;
    public int[][] multiSearch(String big, String[] smalls) {
        root = new TrieNode('*');
        for (String small : smalls) {
            root.insert(small);
        }

        Map<String, List<Integer>> map = root.multiSearch(big);
        int[][] ret = new int[smalls.length][];
        for (int i = 0; i < smalls.length; i++) {
            List<Integer> list = map.get(smalls[i]);
            if (list == null) {
                ret[i] = new int[0];
            } else {
                ret[i] = list.stream().mapToInt(Integer::intValue).toArray();
            }
        }
        return ret;
    }

    private class TrieNode {
        private char c;
        private Map<Character, TrieNode> children;
        private boolean isWord;
        private String word;

        private TrieNode(char c) {
            this.c = c;
            children = new HashMap<>();
            isWord = false;
        }

        private void insert(String s) {
            TrieNode cur = this;
            int n = s.length();
            for (int i = 0; i < n; i++) {
                char c = s.charAt(i);
                if (!cur.children.containsKey(c)) {
                    cur.children.put(c, new TrieNode(c));
                }
                cur = cur.children.get(c);
                if (i == n - 1) {
                    cur.isWord = true;
                    cur.word = s;
                }
            }
        }

        private Map<String, List<Integer>> multiSearch(String s) {
            int n = s.length();
            Map<String, List<Integer>> map = new HashMap<>();
            for (int slow = 0; slow < n; slow++) {
                int fast = slow;
                TrieNode node = this;
                while (node != null && fast < n) {
                    node = node.children.get(s.charAt(fast));
                    if (node != null && node.isWord) {
                        String key = node.word;
                        map.computeIfAbsent(key, k -> new ArrayList<>());
                        map.get(key).add(slow);
                    }
                    fast++;
                }
            }
            return map;
        }

        private TrieNode getLastNode(String s, int start, int end) {
            TrieNode cur = this;
            for (int i = start; i <= end; i++) {
                char c = s.charAt(i);
                if (!cur.children.containsKey(c)) {
                    return null;
                }
                cur = cur.children.get(c);
            }
            return cur;
        }
    }
}
