class Solution {
    public int makeConnected(int n, int[][] connections) {
        UnionFind u = new UnionFind(n);
        int count = 0;
        for (int[] con : connections) {
            if (u.find(con[0]) == u.find(con[1])) {
                count++;
            } else {
                u.union(con[0], con[1]);
            }
        }

        return u.count - 1 <= count ? u.count - 1 : -1;
    }

    private class UnionFind {
        private int[] pi;
        private int[] rk;
        private int count;

        private UnionFind(int n) {
            pi = new int[n];
            rk = new int[n];
            count = n;
            for (int i = 0; i < n; i++) {
                pi[i] = i;
                rk[i] = 1;
            }
        }

        private int find(int x) {
            int cur = x;
            while (cur != pi[cur]) {
                cur = pi[cur];
            }

            while (cur != pi[x]) {
                int tmp = pi[x];
                pi[x] = cur;
                x = tmp;
            }
            return cur;
        }

        private int union(int x, int y) {
            int a = find(x), b = find(y);
            if (a == b) {
                return a;
            }

            if (rk[a] >= rk[b]) {
                if (rk[a] == rk[b]) {
                    rk[a]++;
                }
                pi[b] = a;
            } else {
                pi[a] = b;
            }
            count--;   
            return pi[a];
        }
    }
}
