class Solution {
    private int[] nums;
    private List<List<Integer>> ret;
    private int target;
    public List<List<Integer>> combinationSum(int[] nums, int target) {
        int n = nums.length;
        this.nums = nums;
        this.target = target;
        ret = new ArrayList<>();
        dfs(0, new ArrayList<>(), 0);
        return ret;
    }

    private void dfs(int index, List<Integer> builder, int curSum) {
        if (curSum == target) {
            ret.add(new ArrayList<>(builder));
            return;
        }

        if (index >= nums.length || curSum > target) {
            return;
        }

        for (int i = index; i < nums.length; i++) {
            builder.add(nums[i]);
            dfs(i, builder, curSum + nums[i]);
            builder.remove(builder.size() - 1);
        }

    }
}
