const findRadius = function(houses, heaters) {
    let minRadius = 0;
    let heatersLen = heaters.length;
    heaters.sort((a, b) => a - b);
    // 2 
    //  1i(heaters[i]house)  2j(j=i+1)
    for(let house of houses) {
        let i = binarySearchRight(heaters, house);
        let j = i + 1;
        // i < 0 
        const leftDistance = i < 0 ? Number.MAX_VALUE : house - heaters[i];
        // j >= heatersLen 
        const rightDistance = j >= heatersLen ? Number.MAX_VALUE : heaters[j] - house;

        const curDistance = Math.min(leftDistance, rightDistance);
        minRadius = Math.max(minRadius, curDistance);
    }
    return minRadius;
};
