    nums.sort((a, b) => a - b);
    //  k  [0,max(nums)min(nums)] 
    let n = nums.length, left = 0, right = nums[n - 1] - nums[0];
    while (left <= right) {
        const mid = Math.floor((left + right) / 2);
        let cnt = 0;
        // j mid  cnt
        //   nums[j]mid  i j  mid  ji
        for (let j = 0; j < n; j++) {
            const i = binarySearch(nums, j, nums[j] - mid);
            cnt += j - i;
        }
        if (cnt >= k) {
            right = mid - 1;
        } else {
            left = mid + 1;
        }
    }
    return left;
};


const binarySearch = (nums, end, target) => {
    let left = 0, right = end;
    while(left <= right) {
        let mid = Math.floor((left + right) / 2);
        if(nums[mid] >= target) {
            right = mid -1;
        }
        else if (nums[mid] < target) {
            left = mid + 1;
        }
    }
    return left;
};
