var swimInWater = function(grid) {
    let left = 0, right = 0;
    const len = grid.length;
    // [left, right]
    grid.forEach(g => {
        g.forEach(x => {
            right = Math.max(right, x)
        })
    });

    let seen = {};
    var isCanSwim = function (mid, x, y) {
        // false
        if (x < 0 || y >= len || y < 0 || x >= len) {
            return false;
        }
        // false
        if (grid[x][y] > mid) {
            return false;
        }
        // true
        if (x == len - 1 && y == len - 1) {
            return true;
        }
        // false
        if (seen[`${x}_${y}`]) {
            return false;
        }
        // 
        seen[`${x}_${y}`] = true;
        // |||
        return isCanSwim(mid, x - 1, y)
            || isCanSwim(mid, x + 1, y)
            || isCanSwim(mid, x, y - 1)
            || isCanSwim(mid, x, y + 1);
    }
    while(left <= right) {
        let mid = Math.floor((left + right) / 2);
        if(isCanSwim(mid, 0, 0)) {
            right = mid - 1;
        }
        else {
            left = mid + 1;
        }
        // seen
        seen = {};
    }
    return left;
};
