const backtrack = (n, row, cols, pie, na) => {
    if (row === n) {
        return 1;
    } else {
        let count = 0;
        for (let i = 0; i < n; i++) {
            const naIndex = row - i;
            const pieIndex = row + i;
            if (cols.has(i) || pie.has(pieIndex) || na.has(naIndex)) {
                continue;
            }
            cols.add(i);
            pie.add(pieIndex);
            na.add(naIndex);
            count += backtrack(n, row + 1, cols, pie, na);
            // 
            cols.delete(i);
            pie.delete(pieIndex);
            na.delete(naIndex);
        }
        return count;
    }
}
