var maxDistance = function(grid) {
    let rows = grid.length;
    let cols = grid[0].length;
    let landQueue = [];

    const isValid = (i, j) => {
        return (
            // 
            (i >= 0 && i < rows) && (j >= 0 && j < cols)
        );
    }

    // 
    for (let i = 0; i < rows; i++) {
        for (let j = 0; j < cols; j++) {
            if (grid[i][j] == 1) {
                landQueue.push([i, j]);
            }
        }
    }
    // -1
    if (landQueue.length === cols * rows || landQueue === 0) {
        return -1
    }

    let distance = -1;
    while(landQueue.length) {
        distance++;
        const size = landQueue.length;
        for(let i = 0; i < size; i++) {
            // 
            let [current_i, current_j] = landQueue.shift();
            // ()
            let dx = [-1, 1, 0, 0];
            let dy = [0, 0, -1, 1]; 
            for (let m = 0; m < dx.length; ++m) {
                const next_i = current_i + dx[m];
                const next_j = current_j + dy[m];
                if (isValid(next_i, next_j) && grid[next_i][next_j] == 0) {
                    grid[next_i][next_j] = 1;
                    landQueue.push([next_i, next_j]);
                }
            }
        }
    }
    return distance;
};
