var maxCandies = function(status, candies, keys, containedBoxes, initialBoxes) {
    // 
    let hasBox = {};
    // 
    let canOpen = {};
    let ans = 0;
    let used = {};
    for (let i = 0; i < status.length; ++i) {
        canOpen[i] = status[i];
    }

    let queue = [];
    // initialBoxes
    for (let initBoxIndex of initialBoxes) {
        // 
        hasBox[initBoxIndex] = 1;
        // 
        if (canOpen[initBoxIndex]) {
            queue.push(initBoxIndex);
            used[initBoxIndex] = true;
            ans += candies[initBoxIndex];
        }
    }

    while(queue.length) {
        // bigBox
        const bigBox = queue.shift();
        for (let key of keys[bigBox]) {
            canOpen[key] = 1;
            // 
            if (!used[key] && hasBox[key]) {
                queue.push(key);
                used[key] = true;
                ans += candies[key];
            }
        }
        for (let boxIndex of containedBoxes[bigBox]) {
            hasBox[boxIndex] = 1;
            // 
            if (!used[boxIndex] && canOpen[boxIndex]) {
                queue.push(boxIndex);
                used[boxIndex] = true;
                ans += candies[boxIndex];
            }
        }
    }

    return ans;
};
