var rob = function(nums) {
    let len = nums.length;
    if (len == 0) return 0;
    if (len === 1) return nums[0];
    let dp = new Array(len).fill(0);
    dp[0] = nums[0];
    dp[1] = Math.max(nums[0], nums[1]);
    for (let n = 2; n < len; n++) {
        dp[n] = Math.max(dp[n-1], dp[n-2] + nums[n]);
    }
    return dp[len - 1];
};   
