var canPartition = function(nums) {
    const len = nums.length;
    if (len <= 1) {
        return false;
    }
    var numsSum = nums.reduce(function(sum, current) {
        return sum + current;
    }, 0);
    if (numsSum % 2 == 1) {
        return false;
    }
    // 
    const target = numsSum / 2;
    const dp = new Array(target).fill(0);
    //  0  1 
    if (nums[0] <= target) {
        dp[nums[0]] = true;
    }
    for (let i = 1; i < len; i++) {
        const num = nums[i];
        for (let j = target; j >= num; j--) {
            dp[j] = dp[j] || dp[j - nums[i]];
        }
    }
    return dp[target];
};
