/**
 * @param {number[][]} grid
 * @return {number}
 */
var swimInWater = function(grid) {
     const N = grid.length;
  const directions = [[0,1], [1,0], [0,-1], [-1,0]];
  const times = new Array(N).fill(0).map(() => new Array(N).fill(Infinity));
  times[0][0] = grid[0][0];

  const heap = new MinHeap();
  heap.push({ x: 0, y: 0, time: grid[0][0] });

  while (heap.heap.length > 0) {
    const { x, y, time } = heap.pop();
    if (x === N - 1 && y === N - 1) {
      return time;
    }
    for (const [dx, dy] of directions) {
      const nx = x + dx;
      const ny = y + dy;
      if (nx >= 0 && nx < N && ny >= 0 && ny < N) {
        const maxTime = Math.max(time, grid[nx][ny]);
        if (maxTime < times[nx][ny]) {
          times[nx][ny] = maxTime;
          heap.push({ x: nx, y: ny, time: maxTime });
        }
      }
    }
  }
  return -1;
};

class MinHeap {
  constructor() {
    this.heap = [];
  }

  getParentIndex(i) {
    return Math.floor((i - 1) / 2);
  }

  getLeftChildIndex(i) {
    return 2 * i + 1;
  }

  getRightChildIndex(i) {
    return 2 * i + 2;
  }

  swap(i, j) {
    [this.heap[i], this.heap[j]] = [this.heap[j], this.heap[i]];
  }

  push(node) {
    this.heap.push(node);
    let index = this.heap.length - 1;
    let parent = this.getParentIndex(index);
    while (index > 0 && this.heap[parent].time > this.heap[index].time) {
      this.swap(parent, index);
      index = parent;
      parent = this.getParentIndex(index);
    }
  }

  pop() {
    const min = this.heap[0];
    this.heap[0] = this.heap.pop();
    this.heapify(0);
    return min;
  }

  heapify(i) {
    const left = this.getLeftChildIndex(i);
    const right = this.getRightChildIndex(i);
    let smallest = i;
    if (left < this.heap.length && this.heap[left].time < this.heap[smallest].time) {
      smallest = left;
    }
    if (right < this.heap.length && this.heap[right].time < this.heap[smallest].time) {
      smallest = right;
    }
    if (smallest !== i) {
      this.swap(i, smallest);
      this.heapify(smallest);
    }
  }
}

