class UnionFind {
    constructor(n) {
        // 
        this.parent = new Array(n).fill(0).map((_, index) => index);
        //  n
        this.count = n;
    }

    //  x 
    find(x) {
        if (this.parent[x]!== x) {
            // 
            this.parent[x] = this.find(this.parent[x]);
        }
        return this.parent[x];
    }

    //  x  y 
    union(x, y) {
        const rootX = this.find(x);
        const rootY = this.find(y);
        if (rootX!== rootY) {
            //  rootX  rootY
            this.parent[rootX] = rootY;
            //  1
            this.count--;
        }
    }
}

