class Solution {
public:
    string decodeString(string s) {
        int now = 0;
        return dfs(s, now);
    }
    string dfs(string& s, int& now) {
        string res = "";
        while (now < s.size() && s[now] != ']') {
            if ('A' <= s[now] && s[now] <= 'Z' || 'a' <= s[now] && s[now] <= 'z')  res += s[now++];
            else if ('0' <= s[now] && s[now] <= '9') {
                int i = now, num = 0;
                while (i < s.size() && s[i] != '[') {
                    num = num * 10 + s[i] - '0';
                    i++;
                }
                now = i + 1;
                string temp = dfs(s, now);
                while (num--) res += temp;
                now++;
            }
        }
        return res;
    }
};
