class Solution {
public:
    int minSubarray(vector<int>& nums, int p) {
        int n = nums.size();
        vector<int> s(n + 1);
        for (int i = 1; i <= n; i++) s[i] = (s[i - 1] + nums[i - 1]) % p;  
        int sum = s[n];
        if (sum == 0) return 0;

        unordered_map<int, int> last;
        last[0] = 0;
        int res = n;
        for (int i = 1; i <= n; i++) {
            int mod = (s[i] - sum + p) % p; 
            if (last.count(mod)) res = min(res, i - last[mod]);
            last[s[i]] = i; 
        } 
        return res == n ? -1 : res;
    }
};
