        int l = 0, r = nums.size(), ans = nums.size(); //[a, b]
        while (l < r) {
            int mid = (l + r) >> 1;
            if (nums[mid] == target) {
                ans = mid;
                break;
            }
            else if (nums[mid] > target) r = mid;
            else l = mid + 1;
        }
