class Solution {
public:
    double new21Game(int n, int k, int maxPts) {
        if (!k) return 1;
        memset(dp, 0, sizeof dp);
        for (int i = k; i <= n && i < k + maxPts; i++) dp[i] = 1;
        dp[k-1] = 0;
        for (int i = 1; i<= maxPts; i++) dp[k-1] += dp[k-1+i] / (double)maxPts;
        for (int i = k - 2; i >= 0; i--) {
            dp[i] = dp[i+1] + (dp[i+1] - dp[i+maxPts+1]) / (double)maxPts;
        }
        return dp[0];
    }
};
