                    maxConn = Math.max(maxConn, tot);
                }
            }
        }
        return maxConn;
    }
    public int dfs(int row, int col, int m, int n, int[][] vis, int[][] grid) { 
        int tot = 1;
        for (int i = 0; i < 4; i++) {
            int nx = row + dx[i];
            int ny = col + dy[i];

            if (check(nx, ny, m, n) && grid[nx][ny] == 1) {
                if (vis[nx][ny] == 1) {
                    continue;
                }
                vis[nx][ny] = 1; 
                tot += dfs(nx, ny, m, n, vis, grid);
            }
        }
        return tot;
    }
    boolean check(int row, int col, int m, int n) {
        if ((0 <= row && row < m) && (0 <= col && col < n)) {
            return true;
        } 
        return false;
    }
}
