class Solution {
    private int[] dx = new int[]{0,0,-1,1};
    private int[] dy = new int[]{-1,1,0,0};
    public int maxDistance(int[][] grid) {
        int n = grid.length;
        int[][] dis = new int[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                dis[i][j] = -1;
            }
        }
        Queue<Point> queue = new ArrayDeque<>();
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1) {
                    dis[i][j] = 0;
                    queue.offer(new Point(i, j));
                }
            }
        }

        boolean flag = false;
        Point pp = null;
        while (!queue.isEmpty()) {
            pp = queue.poll();
            int x = pp.x;
            int y = pp.y;
            for (int i = 0; i < 4; i++) {
                int nx = x + dx[i];
                int ny = y + dy[i];
                if (nx < 0 || nx >= n || ny < 0 || ny >= n || dis[nx][ny] != -1) {
                    continue;
                }
                dis[nx][ny] = dis[x][y] + 1;
                flag = true;
                queue.offer(new Point(nx, ny));
            }
        }
        if (pp == null || !flag) {
            return -1;
        }
        int maxDis = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                maxDis = Math.max(maxDis, dis[i][j]);
            }
        }
        return maxDis;
    }

    static class Point {
        int x;
        int y;
        Point() {};
        Point(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }
}
