class Solution {
    public int findNumberOfLIS(int[] nums) {
        int n = nums.length;
        int[] dp = new int[n];  // 0...i, i
        int[] f = new int[n];   // 0...i, i
        for (int i = 0; i < n; i++) {
            dp[i] = 1;
            f[i] = 1;
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < i; j++) {
                if (nums[j] < nums[i]) {
                    if (dp[j] + 1 == dp[i]) {
                        f[i] += f[j];
                    } else if (dp[j] + 1 > dp[i]) {
                        f[i] = f[j];
                    }
                    dp[i] = Math.max(dp[i], dp[j] + 1);
                }
            }
        }
        int maxLen = -1;
        for (int i = 0; i < n; i++) {
            maxLen = Math.max(maxLen, dp[i]);
        }
        int res = 0;
        for (int i = 0; i < n; i++) {
            if (dp[i] == maxLen) {
                res += f[i];
            }
        }
        return res;
    }
}
