        dp = new int[m][n];
        return dfs(0, 0, m-1, n-1);
    }
    int dfs(int x, int y, int m, int n) {
        if (x < 0 || x > m || y < 0 || y > n) {
            return 0;
        }
        if (x == m && y == n) {
            return 1; 
        }
        if (dp[x][y] != 0) {
            return dp[x][y];
        }
        return dp[x][y] = dfs(x+1, y, m, n) + dfs(x, y+1, m, n);
