    double dfs(int row, int column, int k, int n) {
        if (row < 0 || row >= n || column < 0 || column >= n) {
            return 0.0;
        }
        if (k == 0) {
            return 1.0;
        }
        if (dp[row][column][k] != 0.0) {
            return dp[row][column][k];
        }
        double prob = 0.0;
        for (int i = 0; i < 8; i++) {
            int nr = row + dx[i];
            int nc = column + dy[i];
            prob += (double) 1 * dfs(nr, nc, k-1, n) / 8;
        }
        return dp[row][column][k] = prob;
    }
