        int ans = 0, pos = 0;
        std::sort(a.begin(), a.end());
        std::sort(b.begin(), b.end());
        for (int i = 0; i < a.size(); i++) {
            if (pos >= b.size()) {
                break;
            }
            auto it = std::lower_bound(b.begin() + pos, b.end(), a[i]);
